import{_ as r,c as t,a3 as e,o as a}from"./chunks/framework.C4Vvvhxx.js";const u=JSON.parse('{"title":"公共参数","description":"","frontmatter":{},"headers":[],"relativePath":"guide/drivers/common.md","filePath":"zh/guide/drivers/common.md"}'),n={name:"guide/drivers/common.md"};function l(s,o,i,d,c,g){return a(),t("div",null,o[0]||(o[0]=[e('<h1 id="公共参数" tabindex="-1">公共参数 <a class="header-anchor" href="#公共参数" aria-label="Permalink to &quot;公共参数&quot;">​</a></h1><h2 id="基础参数" tabindex="-1">基础参数 <a class="header-anchor" href="#基础参数" aria-label="Permalink to &quot;基础参数&quot;">​</a></h2><ul><li><strong>存储类型</strong>: 可选本地存储、S3、Webdav、FTP、SFTP、SMB、又拍云、Alist、Mirror</li><li><strong>存储名称</strong>: -</li><li><strong>存储状态</strong>: 当存储状态未激活时，该存储不会显示到文件列表中</li><li><strong>挂载目录</strong>: 指文件列表显示的目录，默认挂载到根目录 <code>/</code>，如果想要挂载某个存储的指定目录，需要设置 <strong>根目录</strong> 而不是 <strong>挂载目录</strong></li></ul><h2 id="更多设置" tabindex="-1">更多设置 <a class="header-anchor" href="#更多设置" aria-label="Permalink to &quot;更多设置&quot;">​</a></h2><ul><li><strong>根目录</strong>: 挂载存储内的某个具体目录，比如 <strong>Alist</strong> 存储，根目录为 <code>/移动硬盘</code>, 则访问Alist存储时只能访问<code>/移动硬盘</code>下的文件</li></ul><h3 id="文件加密" tabindex="-1">文件加密 <a class="header-anchor" href="#文件加密" aria-label="Permalink to &quot;文件加密&quot;">​</a></h3><ul><li><strong>文件加密</strong>：是否激活文件加密</li><li><strong>文件密码</strong>：文件加密密码，不能为空</li><li><strong>文件后缀</strong>：默认为空，当设置为 <code>.bin</code> 时，加密后的文件，文件名称会自动添加 <code>.bin</code> 后缀</li><li><strong>文件密码加密</strong>：默认和文件密码相同，类似二次密码</li><li><strong>文件名称加密</strong>：默认不加密，文件名称将会以明文显示(但内容是加密的)，激活后加密后的名称会出现 <code>LKXPGd-ub3Jyu9msrZM5F2EETxxx</code> 等形式</li><li><strong>目录名称加密</strong>：默认不加密，加密后的名称同上</li></ul><h3 id="文件压缩" tabindex="-1">文件压缩 <a class="header-anchor" href="#文件压缩" aria-label="Permalink to &quot;文件压缩&quot;">​</a></h3><ul><li><strong>文件压缩</strong>：是否激活文件压缩</li><li><strong>压缩水平</strong>：可选<strong>默认</strong>、<strong>压缩率最高</strong>、<strong>压缩速度最快</strong>三种方式</li></ul><h3 id="回收站" tabindex="-1">回收站 <a class="header-anchor" href="#回收站" aria-label="Permalink to &quot;回收站&quot;">​</a></h3><ul><li><strong>回收站</strong>：是否激活回收站</li><li><strong>回收站路径</strong>：默认为空，为空时将会在存储的根路径创建一个名为 <code>.maplerecycle</code> 的目录。回收站路径应当尽量避免使用常用的子路径，比如常用 <code>/a/b</code>, 则不要设置回收站路径为 <code>/a/b</code> 目录下的路径，否则删除目录转为移动目录时会出现问题</li></ul>',11)]))}const m=r(n,[["render",l]]);export{u as __pageData,m as default};
