import{_ as e,c as o,a3 as t,o as c}from"./chunks/framework.C4Vvvhxx.js";const b=JSON.parse('{"title":"常见问题","description":"","frontmatter":{},"headers":[],"relativePath":"guide/faq.md","filePath":"zh/guide/faq.md"}'),r={name:"guide/faq.md"};function d(n,a,s,i,h,l){return c(),o("div",null,a[0]||(a[0]=[t('<h1 id="常见问题" tabindex="-1">常见问题 <a class="header-anchor" href="#常见问题" aria-label="Permalink to &quot;常见问题&quot;">​</a></h1><h2 id="看不到添加存储的按钮" tabindex="-1">看不到添加存储的按钮？ <a class="header-anchor" href="#看不到添加存储的按钮" aria-label="Permalink to &quot;看不到添加存储的按钮？&quot;">​</a></h2><p>可能需要往下拉一拉或者滑一滑。。。</p><h2 id="挂载目录和根目录的区别" tabindex="-1">挂载目录和根目录的区别？ <a class="header-anchor" href="#挂载目录和根目录的区别" aria-label="Permalink to &quot;挂载目录和根目录的区别？&quot;">​</a></h2><p><strong>挂载目录</strong>是指红枫云盘挂载位置的显示目录，比如一个名为 <strong>bbb</strong> 的存储，挂载目录为 <code>/</code> 表示该存储将会在软件第一页显示，打开 <code>bbb</code> 即可访问该存储；挂载目录为 <code>/aaa/ccc</code> 表示该存储将会挂载并显示到 <code>/aaa/ccc/bbb</code> 位置，只能通过打开<code>aaa</code> -&gt; <code>ccc</code> -&gt; <code>bbb</code> 才能访问到该存储</p><p><strong>根目录</strong>是指当前存储的指定子目录</p><h2 id="如何挂载存储的指定目录" tabindex="-1">如何挂载存储的指定目录？ <a class="header-anchor" href="#如何挂载存储的指定目录" aria-label="Permalink to &quot;如何挂载存储的指定目录？&quot;">​</a></h2><p>比如挂载本地目录的某个子目录，在设置好本地目录 <code>/home/xxx/app</code> 后，选择 <strong>更多设置</strong>，然后添加想要挂载的子目录，比如 <code>/a/b</code>，设置好后存储就只能访问 <code>/home/xxx/app/a/b</code> 目录下的文件</p>',8)]))}const f=e(r,[["render",d]]);export{b as __pageData,f as default};
