import{_ as r}from"./chunks/more-setting.B_qlQlXF.js";import{_ as o,c as e,a3 as s,o as n}from"./chunks/framework.C4Vvvhxx.js";const a="/images/features/encrypt-01.png",_=JSON.parse('{"title":"文件加密","description":"","frontmatter":{},"headers":[],"relativePath":"guide/features/encrypt.md","filePath":"zh/guide/features/encrypt.md"}'),i={name:"guide/features/encrypt.md"};function g(c,t,l,d,p,m){return n(),e("div",null,t[0]||(t[0]=[s('<h1 id="文件加密" tabindex="-1">文件加密 <a class="header-anchor" href="#文件加密" aria-label="Permalink to &quot;文件加密&quot;">​</a></h1><p>红枫云盘采用 <code>AES</code> 加密算法，自动对上传或者复制到此存储的文件进行加密，客户端查看或下载时自动解密，再也不用担心上传的视频变成<strong>8秒教育片</strong></p><h2 id="使用" tabindex="-1">使用 <a class="header-anchor" href="#使用" aria-label="Permalink to &quot;使用&quot;">​</a></h2><p>首先打开红枫云盘，找到 <strong>设置 -&gt; 存储库</strong>，选择已有存储或者添加新的存储，在设置好其它参数后选择 <strong>更多设置</strong></p><p><img src="'+r+'" alt=""><img src="'+a+'" alt=""></p><p>相关参数:</p><ul><li><strong>文件加密</strong>：是否激活文件加密</li><li><strong>文件密码</strong>：文件加密密码，不能为空</li><li><strong>文件后缀</strong>：默认为空，当设置为 <code>.bin</code> 时，加密后的文件，文件名称会自动添加 <code>.bin</code> 后缀</li><li><strong>文件密码加密</strong>：默认和文件密码相同，类似二次密码</li><li><strong>文件名称加密</strong>：默认不加密，文件名称将会以明文显示(但内容是加密的)，激活后加密后的名称会出现 <code>LKXPGd-ub3Jyu9msrZM5F2EETxxx</code> 等形式</li><li><strong>目录名称加密</strong>：默认不加密，加密后的名称同上</li></ul><p id="empty-dir"><strong>注意</strong>: 在设置文件加密前请先在对应的存储创建一个<strong>空目录</strong>，因为红枫云盘的文件加密功能类似加密空间，无法对某些文件加密，某些文件不加密，只能是<strong>要么全部加密</strong>，<strong>要么全部不加密</strong>，并且开启后<strong>不能修改加密相关的配置</strong>，否则将会导致无法打开已加密的文件，包括预览，下载，复制，移动等功能都会失效</p>',8)]))}const h=o(i,[["render",g]]);export{_ as __pageData,h as default};
